mapper.insert:
INSERT INTO behavior_detail (
    id, user_id, behavior_type, target_id, target_type,
    behavior_content, device_no, ip_addr, create_time, is_valid
) VALUES (
             #{id}, #{userId}, #{behaviorType}, #{targetId}, #{targetType},
             #{behaviorContent}, #{deviceNo}, #{ipAddr}, #{createTime}, #{isValid}
         )

INSERT INTO behavior_detail (
id, user_id, behavior_type, target_id, target_type,
behavior_content, device_no, ip_addr, create_time, is_valid
) VALUES

    (
    #{item.id}, #{item.userId}, #{item.behaviorType}, #{item.targetId}, #{item.targetType},
    #{item.behaviorContent}, #{item.deviceNo}, #{item.ipAddr}, #{item.createTime}, #{item.isValid}
    )

mapper.update:
UPDATE behavior_detail
user_id = #{userId}, behavior_type = #{behaviorType}, target_id = #{targetId}, target_type = #{targetType}, behavior_content = #{behaviorContent}, device_no = #{deviceNo}, ip_addr = #{ipAddr}, is_valid = #{isValid}
WHERE id = #{id}

update.foreach:
UPDATE behavior_detail
user_id = #{item.userId}, behavior_type = #{item.behaviorType}, target_id = #{item.targetId}, target_type = #{item.targetType}, behavior_content = #{item.behaviorContent}, device_no = #{item.deviceNo}, ip_addr = #{item.ipAddr}, is_valid = #{item.isValid}
WHERE id = #{item.id}
