mapper.insert:
INSERT INTO hot_behavior (
    id, target_id, target_type, behavior_count,
    browse_count, click_count, collect_count, update_time
) VALUES (
             #{id}, #{targetId}, #{targetType}, #{behaviorCount},
             #{browseCount}, #{clickCount}, #{collectCount}, #{updateTime}
         )

INSERT INTO hot_behavior (
id, target_id, target_type, behavior_count,
browse_count, click_count, collect_count, update_time
) VALUES

    (
    #{item.id}, #{item.targetId}, #{item.targetType}, #{item.behaviorCount},
    #{item.browseCount}, #{item.clickCount}, #{item.collectCount}, #{item.updateTime}
    )

mapper.update:
UPDATE hot_behavior
target_id = #{targetId}, target_type = #{targetType}, behavior_count = #{behaviorCount}, browse_count = #{browseCount}, click_count = #{clickCount}, collect_count = #{collectCount}, update_time = #{updateTime}
WHERE id = #{id}

update.foreach:
UPDATE hot_behavior
target_id = #{item.targetId}, target_type = #{item.targetType}, behavior_count = #{item.behaviorCount}, browse_count = #{item.browseCount}, click_count = #{item.clickCount}, collect_count = #{item.collectCount}, update_time = #{item.updateTime}
WHERE id = #{item.id}
